(function () {
    var util = require('util');
    
    module.exports.buildroutes = function(xmlfile){
        
var r = xmlfile.get('//routes');
var rchildren = r.childNodes();
var nbrofroutes = rchildren.length;
        console.log(util.inspect(rchildren));
    for (var u = 0; u < nbrofroutes; u++){
    var t = rchildren[u];
    var tpath = t.attr('path');
        console.log('path found : ' + tpath);
    }
    }
    
}());
